import keyboard
from crosshair_window import CrosshairWindow
import customtkinter as ctk
from tkinter import colorchooser, messagebox
from constants import SUPPORTED_SHAPES
import base64

# Base64 encoded icon data
ICON_DATA = """

"""

class MainAppUI(ctk.CTk):
    def __init__(self, app):
        super().__init__()
        self.app = app  # Reference to the main application instance
        self.title("Halal ESP")
        self.geometry("450x420")
        self.minsize(430, 400)
        self.set_app_icon()  # Set the icon
        self.init_ui()

    def set_app_icon(self):
        try:
            icon_bytes = base64.b64decode(ICON_DATA)
            # Windows and macOS need different approaches for setting the icon
            if self.tk.call('tk', 'windowingsystem') == 'win32':
                import io
                from PIL import Image
                icon_stream = io.BytesIO(icon_bytes)
                image = Image.open(icon_stream)
                image.save('icon.ico', format='ICO', sizes=[(256, 256)])
                self.wm_iconbitmap('icon.ico')
                import os
                os.remove('icon.ico') # Clean up temporary icon file
            else:
                # For macOS and Linux (Tkinter/ctk supports PNG icons)
                icon_file = 'icon.png'
                with open(icon_file, 'wb') as f:
                    f.write(icon_bytes)
                self.iconphoto(False, ctk.CTkImage(file=icon_file))
                import os
                os.remove(icon_file) # Clean up temporary icon file

        except Exception as e:
            print(f"Error setting app icon: {e}")

    def init_ui(self):
        self.grid_columnconfigure(0, weight=1)
        self.grid_rowconfigure(1, weight=1)

        # Header Frame
        header_frame = ctk.CTkFrame(self, corner_radius=0, fg_color="gray15")
        header_frame.grid(row=0, column=0, padx=0, pady=(0, 5), sticky="ew")
        header_frame.columnconfigure(0, weight=1)
        self.toggle_button = ctk.CTkButton(header_frame, text="Enable Crosshair", command=self.app.toggle_crosshair,
                                            font=ctk.CTkFont(weight="bold"))
        self.toggle_button.grid(row=0, column=0, padx=10, pady=10, sticky="ew")

        # Settings Container Frame
        settings_container_frame = ctk.CTkFrame(self)
        settings_container_frame.grid(row=1, column=0, padx=10, pady=5, sticky="nsew")
        settings_container_frame.grid_columnconfigure(0, weight=1)

        # Appearance Settings Frame
        appearance_frame = ctk.CTkFrame(settings_container_frame)
        appearance_frame.grid(row=0, column=0, padx=10, pady=10, sticky="ew")
        appearance_frame.columnconfigure(1, weight=1)
        ctk.CTkLabel(appearance_frame, text="Appearance Settings", font=ctk.CTkFont(weight="bold")).grid(row=0, column=0, columnspan=2, padx=5, pady=(5, 10), sticky="ew")

        # Shape
        shape_label = ctk.CTkLabel(appearance_frame, text="Shape:")
        shape_label.grid(row=1, column=0, padx=10, pady=5, sticky="w")
        self.shape_combobox = ctk.CTkComboBox(appearance_frame, values=SUPPORTED_SHAPES,
                                             command=self._update_crosshair_from_ui)
        self.shape_combobox.set(self.app.settings_manager.settings["shape"])
        self.shape_combobox.grid(row=1, column=1, padx=10, pady=5, sticky="ew")

        # Color
        color_label = ctk.CTkLabel(appearance_frame, text="Color:")
        color_label.grid(row=2, column=0, padx=10, pady=5, sticky="w")
        self.color_button = ctk.CTkButton(appearance_frame, text="Choose Color", command=self.change_crosshair_color,
                                         fg_color=self.app.settings_manager.settings["color"], width=120)
        self.color_button.grid(row=2, column=1, padx=10, pady=5, sticky="ew")

        # Scale
        scale_label = ctk.CTkLabel(appearance_frame, text="Scale:")
        scale_label.grid(row=3, column=0, padx=10, pady=5, sticky="w")
        self.scale_slider = ctk.CTkSlider(appearance_frame, from_=1, to=20, command=self._update_crosshair_from_ui)
        self.scale_slider.set(self.app.settings_manager.settings["scale"] * 10)
        self.scale_slider.grid(row=3, column=1, padx=10, pady=5, sticky="ew")

        # Line Thickness
        thickness_label = ctk.CTkLabel(appearance_frame, text="Thickness:")
        thickness_label.grid(row=4, column=0, padx=10, pady=5, sticky="w")
        self.thickness_spinbox = ctk.CTkEntry(appearance_frame)
        self.thickness_spinbox.insert(0, str(self.app.settings_manager.settings["line_width"]))
        self.thickness_spinbox.grid(row=4, column=1, padx=10, pady=5, sticky="ew")
        self.thickness_spinbox.bind("<KeyRelease>", self._update_crosshair_from_ui)

        # Hotkey Settings Frame
        hotkey_frame = ctk.CTkFrame(settings_container_frame)
        hotkey_frame.grid(row=1, column=0, padx=10, pady=10, sticky="ew")
        hotkey_frame.columnconfigure(1, weight=1)
        ctk.CTkLabel(hotkey_frame, text="Hotkey Settings", font=ctk.CTkFont(weight="bold")).grid(row=0, column=0, columnspan=2, padx=5, pady=(5, 10), sticky="ew")

        # Hotkey
        hotkey_label = ctk.CTkLabel(hotkey_frame, text="Toggle Crosshair:")
        hotkey_label.grid(row=1, column=0, padx=10, pady=5, sticky="w")
        self.hotkey_entry = ctk.CTkEntry(hotkey_frame)
        self.hotkey_entry.insert(0, self.app.settings_manager.settings["hotkey"])
        self.hotkey_entry.grid(row=1, column=1, padx=10, pady=5, sticky="ew")
        self.hotkey_entry.bind("<FocusOut>", self.update_hotkey)

        # Footer Frame
        footer_frame = ctk.CTkFrame(self, corner_radius=0, fg_color="gray15")
        footer_frame.grid(row=2, column=0, padx=0, pady=(5, 0), sticky="ew")
        footer_frame.columnconfigure(0, weight=1)
        footer_frame.columnconfigure(1, weight=1)

        self.reset_button = ctk.CTkButton(footer_frame, text="Reset Settings", command=self.app.reset_settings)
        self.reset_button.grid(row=0, column=0, padx=10, pady=10, sticky="ew")
        self.exit_button = ctk.CTkButton(footer_frame, text="Exit Application", command=self.app.close_application)
        self.exit_button.grid(row=0, column=1, padx=10, pady=10, sticky="ew")

        # Bind the combobox directly to update_crosshair
        self.shape_combobox.bind("<<ComboboxSelected>>", self._update_crosshair_from_ui)

        # تحديث لون زر "Choose Color" عند بدء التشغيل
        self.update_color_button()

    def update_toggle_button_text(self):
        if self.app.crosshair_manager.is_active():
            self.toggle_button.configure(text="Hide Crosshair")
        else:
            self.toggle_button.configure(text="Enable Crosshair")

    def _update_crosshair_from_ui(self, event=None):
        try:
            line_width = int(self.thickness_spinbox.get())
            if line_width <= 0:
                raise ValueError("Line thickness must be a positive integer.")
            self.app.settings_manager.settings["line_width"] = line_width
        except ValueError as e:
            messagebox.showerror("Invalid Input", str(e))
            self.thickness_spinbox.delete(0, "end")
            self.thickness_spinbox.insert(0, str(self.app.settings_manager.settings["line_width"]))
            return

        self.app.settings_manager.settings["shape"] = self.shape_combobox.get()
        self.app.settings_manager.settings["scale"] = self.scale_slider.get() / 10.0
        self.app.crosshair_manager.update_crosshair()
        self.app.settings_manager.save()

    def change_crosshair_color(self):
        color_code = colorchooser.askcolor(title="Select Crosshair Color", initialcolor=self.app.settings_manager.settings["color"])[1]
        if color_code:
            self.app.settings_manager.settings["color"] = color_code
            self.update_color_button()  # تحديث لون الزر هنا
            self.app.crosshair_manager.update_crosshair()
            self.app.settings_manager.save()

    def update_color_button(self):
        self.color_button.configure(fg_color=self.app.settings_manager.settings["color"])

    def update_hotkey(self, event=None):
        new_hotkey = self.hotkey_entry.get().lower() # Normalize input
        if new_hotkey:
            try:
                keyboard.remove_hotkey(self.app.settings_manager.settings["hotkey"])
            except KeyError:
                pass  # Ignore if the old hotkey doesn't exist

            try:
                keyboard.add_hotkey(new_hotkey, self.app.toggle_crosshair)
                self.app.settings_manager.settings["hotkey"] = new_hotkey
                self.app.settings_manager.save()
            except ValueError as e:
                messagebox.showerror("Invalid Hotkey", str(e))
                self.hotkey_entry.delete(0, "end")
                self.hotkey_entry.insert(0, self.app.settings_manager.settings["hotkey"])
                return

    def update_ui_from_settings(self):
        self.shape_combobox.set(self.app.settings_manager.settings["shape"])
        self.scale_slider.set(self.app.settings_manager.settings["scale"] * 10)
        self.thickness_spinbox.delete(0, "end")
        self.thickness_spinbox.insert(0, str(self.app.settings_manager.settings["line_width"]))
        self.hotkey_entry.delete(0, "end")
        self.hotkey_entry.insert(0, self.app.settings_manager.settings["hotkey"])
        self.update_color_button()
        self.update_toggle_button_text()
